mapper.insert:
insert into BOARDS
(
board_cat_name
, user_nickname
, board_title
, board_content

    , file_name




)
values
(
#{board.catName}

, '김정정'
, #{board.title}
, #{board.content, jdbcType=VARCHAR}

    , #{board.filename}




)

mapper.select:
select * from ( select
, b.board_no as no , b.board_cat_name as catName , b.board_title as title , b.board_content as content , b.board_cnt as viewCnt , b.board_like as `like` , b.is_deleted as isDeleted , b.board_created_date as createdDate , u.user_nickname as `user.nickname` from BOARDS b join USERS u on b.user_nickname = u.user_nickname where b.is_deleted = 'N' ) as board where rn between #{condition.begin} and #{condition.end}
order by rn

mapper.update:
update
  BOARDS
set
  board_cat_name = #{board.catName}
  , board_title = #{board.title}
  , board_content = #{board.content}
  , BOARD_UPDATED_DATE = sysdate()
  , is_deleted = #{board.isDeleted}
where board_no = #{board.no}
