mapper.insert:
insert into CREWS
(
crew_schedule
, crew_location
, crew_title
, crew_name
, crew_description

    , crew_thumbnail

, crew_updated_date

  , file_name

)
values
(
#{crew.schedule}
, #{crew.location}
, #{crew.title}
, #{crew.name}
, #{crew.description}

  , #{crew.thumbnail.saveName}

, null

  , #{crew.uploadFile.saveName}

)

  SELECT LAST_INSERT_ID()

insert into CREW_MEMBERS
( crew_no
, is_reader
, crew_join_date
, is_join
, user_no)
values ( #{member.crewNo}
       , #{member.reader}
       , #{member.joinDate}
       , #{member.join}
       , #{member.user.no})

mapper.update:
update CREWS
set crew_view_cnt = #{crew.viewCnt}
where crew_no = #{crew.no}

update CREWS
set crew_title = #{crew.title}
    , crew_description = #{crew.description}
    , crew_schedule = #{crew.schedule}
    , crew_location = #{crew.location}
    , crew_name = #{crew.name}
    , crew_thumbnail = #{crew.thumbnail.saveName}
    , crew_updated_date = sysdate()

    , file_name = #{crew.uploadFile.saveName}

    , is_deleted = #{crew.deleted}
where
    crew_no = #{crew.no}
