mapper.select:
select count(*)
from COURSE_RECORDS


        and course_no = #{condition.courseNo}


        and user_no = #{condition.userNo}

select course_no as "course.no"
     , user_no as "user.no"
from COURSE_LIKES
where course_no = #{courseNo}
and user_no = #{userNo}

mapper.update:
update COURSES
set course_like_cnt = #{likeCount}
where course_no = #{courseNo}

mapper.delete:
delete from COURSE_LIKES
where user_no = #{userNo}
and course_no = #{courseNo}

mapper.select:
select * from ( select dense_rank() over (order by r.finished_time) as no , row_number() over (order by r.finished_time) as rn , c.course_no , c.course_name as "course.name" , c.course_distance as "course.distance" , c.course_level as "course.level" , r.finished_no , r.finished_date as finishedDate , r.finished_time as finishedTime , r.course_no as "course.no" , u.user_no as "user.no" , u.user_nickname as "user.nickname" from COURSES c inner join COURSE_RECORDS r on c.course_no = r.course_no inner join USERS u on r.user_no = u.user_no
course_no = #{condition.courseNo}
and r.user_no = #{condition.userNo}
) ar where rn between #{condition.begin} and #{condition.end}
