mapper.select:
select R.LESSON_RESERVATION_NO as "no" , R.USER_NO as "user.no" , R.LESSON_RESERVATION_CREATED_DATE as reservationCreatedDate , R.LESSON_RESERVATION_UPDATED_DATE as reservationUpdatedDate , L.LESSON_NO as "lesson.lessonNo" , L.LESSON_TITLE as "lesson.title" , L.LESSON_PRICE as "lesson.price" , L.LESSON_SUBJECT as "lesson.subject" , L.USER_NO as "user.no" , L.lesson_status as "lesson.status" , date_format(L.lesson_created_date, '%Y-%m-%d') , U.USER_NAME as "lesson.lecturer.name" from LESSON_RESERVATIONS R INNER JOIN LESSONS L ON R.LESSON_NO = L.LESSON_NO INNER JOIN USERS U ON L.USER_NO = U.USER_NO where R.USER_NO = #{userNo}
AND U.USER_NAME LIKE CONCAT('%', #{condition.searchKeyword}, '%')
AND L.LESSON_TITLE LIKE CONCAT('%', #{condition.searchKeyword}, '%')
AND L.LESSON_SUBJECT LIKE CONCAT('%', #{condition.searchKeyword}, '%')
AND L.LESSON_STATUS = #{condition.lessonStatus}
AND L.LESSON_SUBJECT = #{condition.lessonSubject}
AND R.LESSON_RESERVATION_CREATED_DATE >= #{condition.startDate}
AND R.LESSON_RESERVATION_CREATED_DATE <= #{condition.endDate}
